import './App.css';
import { useContext } from 'react';
import { AppContext } from './context';

const Cart = () => {
  const { cartItems, removeFromCart } = useContext(AppContext);

  if (cartItems.length === 0) return <div className="cart"><h3>Your cart is empty</h3></div>;

  return (
    <div className="cart">
      <h3>Cart</h3>
      <ul>
        {cartItems.map(item => (
          <li key={item.id}>
            <img src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/${item.cloudinaryImageId}`} alt={item.name} style={{width: "80px"}} />
            <div>
              <p>{item.name}</p>
              <p>â‚¹{item.price}</p>
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Cart;
