import './App.css';
import { useState, useEffect } from "react";

const Card = (props) => {
  const { data } = props;
  const { cloudinaryImageId } = data;  // Note: key is usually 'cloudinaryImageId' for images

  return (
    <img
      src={"https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/" + cloudinaryImageId}
      alt="banner"
      className="logoimage"
    />
  );
};

const Banner = () => {
  const [list, setList] = useState([]);

  const fetchData = async () => {
    const response = await fetch(
      "https://www.swiggy.com/dapi/restaurants/list/v5?lat=23.7956531&lng=86.43038589999999&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING"
    );
    const Data = await response.json();

    // Find the card containing restaurants list
    // Usually cards contain various widgets, restaurants are often inside a nested structure.
    // From recent data, restaurant list can be inside cards[2].card.card.gridElements.infoWithStyle.restaurants

    const cards = Data?.data?.cards || [];

    // Find card that has restaurants list
    let restaurantList = [];

    for (let card of cards) {
      const cardData = card?.card?.card;
      if (cardData?.gridElements?.infoWithStyle?.restaurants) {
        restaurantList = cardData.gridElements.infoWithStyle.restaurants;
        break;
      }
    }

    setList(restaurantList);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <div className="bannerConatiner">
        <h3>What's on your mind?</h3>
      </div>

      <div className="bannerConatiner">
        {list.length > 0 ? (
          list.map((each) => <Card data={each} key={each.id} />)
        ) : (
          <p>No restaurants available.</p>
        )}
      </div>
    </div>
  );
};

export default Banner;
