 import './App.css'; 

import{useState, useEffect} from "react"; 
const TopRes = (props) => {
    const restaurantData = props.data?.info;
    const{name,cloudinaryImageId, avgRating,cuisines,areaName} =restaurantData;
    console.log("test",props)
    return (
        <div className ="toprescard">
            <img src = {"https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/"+cloudinaryImageId} alt ="hvh" className = "deliveryIcons"/> 
            <h3>{name}</h3>
            <h3><img src ="https://banner2.cleanpng.com/20190605/vep/kisspng-macys-herald-square-logo-royalty-free-image-star-png-vector-clipart-psd-peoplepng-com-5cf8639da90d18.0343359515597823016924.jpg" className = "deliveryIcon" alt ="logo"/> {avgRating}   </h3>
            <h4>{cuisines}</h4>
            <h4>{areaName}</h4>
        </div>
    )
}



const RestaurantsLists  = () =>{


    const[latitude, setLatitude] = useState([28.7041]);
    const[longitude, setLongitude] = useState([77.1025]);
    const url = `https://www.swiggy.com/dapi/restaurants/list/v5?lat=${latitude}&lng=${longitude}&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING`

    //     navigator.geolocation.getCurrentPosition((position)=>{
    //     setlatitude(position.coords.latitude)
    //     setlongitude(position.coords.longitude)



    //     console.log(position.coords.latitude)
    //     console.log(position.coords.longitude)

    // }) 
    useEffect(() => {
        // Get location only on first mount
        navigator.geolocation.getCurrentPosition(
            (position) => {
                setLatitude(position.coords.latitude);
                setLongitude(position.coords.longitude);
                console.log(position.coords.latitude, position.coords.longitude);
            },
            (error) => {
                console.error("Error getting location:", error);
            }
        );
    }, [latitude,longitude]);









    const [list, setList] = useState([]); 
    const [topres, settopres] = useState([]);
        const fetchData = async () =>{


            const response = await fetch(url);
            const Data = await response.json(); 
            settopres(Data?.data?.cards[1]?.card?.card?.header?.title)

            setList(Data?.data?.cards[1]?.card?.card?.gridElements?.infoWithStyle?.restaurants)



        }

        useEffect(()=>{ 
        fetchData();
       },[longitude, latitude]) 



    return(
        <div >
            <h3 >{topres}</h3> 
            <div className="toprescontainer">
                {list?.map((each)=> (<TopRes data ={each} key ={each.id}/>))}
            </div>



        </div>
    )
} 

export default RestaurantsLists ;
