// src/api.js

const SWIGGY_BASE = 'https://www.swiggy.com';

export function buildListingUrl(lat, lng) {
  const params = new URLSearchParams({
    lat: String(lat),
    lng: String(lng),
    'is-seo-homepage-enabled': 'true',
    page_type: 'DESKTOP_WEB_LISTING',
  });
  return `${SWIGGY_BASE}/dapi/restaurants/list/v5?${params.toString()}`;
}

// Safe fetch wrapper
async function safeFetchJson(url, { signal } = {}) {
  const res = await fetch(url, {
    method: 'GET',
    headers: {
      'Accept': 'application/json, text/plain, */*',
    },
    signal,
  });
  if (!res.ok) {
    throw new Error(`HTTP ${res.status} for ${url}`);
  }
  return res.json();
}

// High-level: fetch restaurants by location
export async function fetchRestaurants(lat, lng, signal) {
  const url = buildListingUrl(lat, lng);
  const json = await safeFetchJson(url, { signal });
  return {
    restaurants: extractRestaurants(json),
    areaTitle: extractSectionTitle(json)
  };
}

// High-level: fetch a restaurant menu
export async function fetchMenu(restaurantId, lat, lng, signal) {
  const params = new URLSearchParams({
    lat: String(lat),
    lng: String(lng),
    restaurantId: String(restaurantId),
    page_type: 'DESKTOP_MENU_LISTING',
  });
  params.set('page-type', 'REGULAR_MENU');
  params.set('complete-menu', 'true');
  const url = `${SWIGGY_BASE}/dapi/menu/pl?${params.toString()}`;
  return safeFetchJson(url, { signal });
}

/**
 * extractBannerImages(json)
 * Returns array of cloudinaryImageId strings for top banner carousel
 */
export function extractBannerImages(json) {
  const cards = json?.data?.cards ?? [];
  for (const c of cards) {
    const card = c?.card?.card;
    const imgs =
      card?.imageGridCards?.info ||
      card?.gridElements?.infoWithStyle?.info ||
      card?.gridElements?.infoWithStyle?.restaurants ||
      [];
    const candidates = Array.isArray(imgs) ? imgs : [];
    const imageIds = candidates
      .map((it) => it?.imageId || it?.cloudinaryImageId || it?.info?.cloudinaryImageId)
      .filter(Boolean);
    if (imageIds.length) return imageIds;
  }
  return [];
}

/**
 * extractSectionTitle(json)
 * Returns a human-friendly section title, e.g., "Top restaurant chains in ..."
 */
export function extractSectionTitle(json) {
  const cards = json?.data?.cards ?? [];
  for (const c of cards) {
    const title =
      c?.card?.card?.title ||
      c?.card?.card?.header?.title ||
      c?.card?.card?.header?.subtitle;
    if (title) return title;
  }
  return '';
}

/**
 * extractRestaurants(json)
 * Returns an array of { info, ... } entries (Swiggy schema)
 */
export function extractRestaurants(json) {
  const cards = json?.data?.cards ?? [];
  for (const c of cards) {
    const card = c?.card?.card;
    const restaurants = card?.gridElements?.infoWithStyle?.restaurants;
    if (Array.isArray(restaurants) && restaurants.length) {
      return restaurants;
    }
    const altList = card?.data?.cards?.flatMap((cc) => cc?.card?.card?.gridElements?.infoWithStyle?.restaurants || []);
    if (Array.isArray(altList) && altList.length) {
      return altList;
    }
  }
  return [];
}

/**
 * extractMenuItems(json)
 * Returns an array of menu item entries.
 */


// src/api.js
// ... (previous functions remain the same)

export function extractMenuItems(json) {
  const menuItems = [];
  const sections = json?.data?.cards || [];

  // Look for the card that contains the menu sections
  const menuCard = sections.find(
    (card) => card?.groupedCard?.cardGroupMap?.REGULAR?.cards
  );

  // If a menu card is found, extract the items
  if (menuCard) {
    const regularCards = menuCard.groupedCard.cardGroupMap.REGULAR.cards;

    for (const categoryCard of regularCards) {
      const itemsList = categoryCard?.card?.card?.itemCards || categoryCard?.card?.card?.carousel;

      if (itemsList) {
        for (const item of itemsList) {
          const itemData = item?.card?.info;
          if (itemData) {
            menuItems.push({
              id: itemData.id,
              name: itemData.name,
              price: (itemData.price || itemData.defaultPrice) / 100,
              imgId: itemData.cloudinaryImageId
            });
          }
        }
      }
    }
  }

  // Final check in case of a different structure
  if (menuItems.length === 0) {
    const foodItems = json?.data?.menu?.items;
    if (foodItems) {
      for (const key in foodItems) {
        const itemData = foodItems[key];
        menuItems.push({
          id: itemData.id,
          name: itemData.name,
          price: itemData.price / 100,
          imgId: itemData.cloudinaryImageId
        });
      }
    }
  }

  return menuItems;
}
