import './App.css';

import{useState, useEffect} from "react"; 




const Card = (props) =>{
    const {data} = props ;
    const {imageId} = data ; 
    return(
        <img src = {"https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/"+imageId} alt = "banner"  className ="logoimage" /> 
    )
}






const Banner = () => {

    const [list,setList] = useState([]); 
    const fetchData = async () =>{
        const response = await fetch ("https://www.swiggy.com/dapi/restaurants/list/v5?lat=23.7956531&lng=86.43038589999999&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING");
        const Data = await response.json(); 
        setList(Data?.data?.cards[0]?.card.card.imageGridCards.info)

    }



    useEffect(()=>{ 
    fetchData() 

    },[]) 




    return(
        <div>
            <div className="bannerConatiner">
                <h3>What's on your mind?</h3>
            </div>
.
            <div className="bannerConatiner">
                {
                    list.map((each)=>(<Card data ={each} key ={each.id}/>))
                }


            </div> 

        </div>
    )
} 
export default Banner;
