// src/RestaurantMenu.js
import { useEffect, useState } from "react";
import { useParams, NavLink } from "react-router-dom";
import { fetchMenu } from "./api";
import { useApp } from "./context";

export default function RestaurantMenu() {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [items, setItems] = useState([]);
  const [err, setErr] = useState("");

  const { addToCart, userLocation } = useApp();

  useEffect(() => {
    if (!id || !userLocation?.lat || !userLocation?.lng) return;

    const ac = new AbortController();
    setLoading(true);

    fetchMenu(id, userLocation.lat, userLocation.lng, ac.signal)
      .then((json) => {
         // Log the raw JSON response here to see if data is being returned
         console.log('API Response:', json);
         setItems(extractMenuItems(json));
         setErr('');
      })
      .catch(e => {
          if (e.name === 'AbortError') {
              return;
          }
          setErr(e.message);
      })
      .finally(() => setLoading(false));

    return () => ac.abort();
  }, [id, userLocation]);

  const handleAddToCart = (item) => {
      addToCart(item);
  };

  return (
    <div className="menu-page">
      <NavLink to="/" className="back-button">Back to Restaurants</NavLink>
      <h2>Restaurant Menu</h2>
      {loading && <p>Loading menu...</p>}
      {err && <p style={{ color: "#b00" }}>{err}</p>}
      {!loading && !err && items.length === 0 && <p>No menu items found.</p>}
      <ul className="menu-items">
        {items.map(item => (
          <li key={item.id} className="menu-item">
            {item.imgId && (
              <img
                src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_208,h_208,c_fit/${item.imgId}`}
                alt={item.name}
              />
            )}
            <div>
              <p>{item.name}</p>
              <p>â‚¹{item.price}</p>
            </div>
            <button
              onClick={() => handleAddToCart(item)}
              className="add-to-cart-menu-btn"
            >
              Add
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

function extractMenuItems(json) {
    const sections = json?.data?.cards || [];
    const menuItems = [];

    const findItemsInCards = (cards) => {
        if (!Array.isArray(cards)) return;
        for (const card of cards) {
            const itemCards = card?.card?.card?.itemCards || card?.card?.card?.carousel;
            if (itemCards) {
                for (const item of itemCards) {
                    const itemData = item?.card?.info;
                    if (itemData) {
                        menuItems.push({
                            id: itemData.id,
                            name: itemData.name,
                            price: (itemData.price || itemData.defaultPrice) / 100,
                            imgId: itemData.cloudinaryImageId
                        });
                    }
                }
            } else if (card?.card?.card?.cards) {
                findItemsInCards(card.card.card.cards);
            }
        }
    };
    findItemsInCards(sections);

    if (menuItems.length === 0) {
        const foodItems = json?.data?.menu?.items;
        if (foodItems) {
            for (const key in foodItems) {
                const itemData = foodItems[key];
                menuItems.push({
                    id: itemData.id,
                    name: itemData.name,
                    price: itemData.price / 100,
                    imgId: itemData.cloudinaryImageId
                });
            }
        }
    }
    return menuItems;
}
