    if (!res.ok) {
      throw new Error(`HTTP ${res.status} for ${url}`);
    }
    return res.json();
  } catch (error) {
    if (error.name === 'AbortError') {
      // This is expected on component unmount
      console.log(`Fetch aborted for ${url}`);
    } else {
      console.error(`Fetch error for ${url}:`, error);
    }
    throw error;
  }
}

