export function extractMenuItems(json) {
    const menuItems = [];

    // Target the specific deeply nested path
    const menuCards = json?.data?.cards[4]?.groupedCard?.cardGroupMap?.REGULAR?.cards;

    if (menuCards) {
        // We start from index 1 to skip the filter card at index 0
        for (let i = 1; i < menuCards.length; i++) {
            const categoryCard = menuCards[i];
            const itemsList = categoryCard?.card?.card?.itemCards || categoryCard?.card?.card?.carousel;

            if (itemsList) {
                for (const item of itemsList) {
                    const itemData = item?.card?.info;
                    if (itemData) {
                        menuItems.push({
                            id: itemData.id,
                            name: itemData.name,
                            price: (itemData.price || itemData.defaultPrice) / 100,
                            imgId: itemData.cloudinaryImageId
                        });
                    }
                }
            }
        }
    }

    // Log the extracted items to confirm the fix
    console.log("Extracted Menu Items:", menuItems);

    return menuItems;
}
