// RestaurantMenu.js
import { useEffect, useState } from "react";
import { fetchMenu } from "./api";

export default function RestaurantMenu({ restaurant, onClose }) {
  const [loading, setLoading] = useState(true);
  const [items, setItems] = useState([]);
  const [err, setErr] = useState("");

  useEffect(() => {
    if (!restaurant?.id || !restaurant?.lat || !restaurant?.lng) return;
    const ac = new AbortController();
    setLoading(true);
    fetchMenu(restaurant.lat, restaurant.lng, restaurant.id, ac.signal)
      .then(({ items }) => setItems(items))
      .catch(e => setErr(e.message))
      .finally(() => setLoading(false));
    return () => ac.abort();
  }, [restaurant]);

  return (
    <div className="menu-modal">
      <div className="menu-modal-content">
        <button onClick={onClose} style={{ float: "right" }}>Close</button>
        <h2>Menu</h2>
        {loading && <p>Loading menu...</p>}
        {err && <p style={{ color: "#b00" }}>{err}</p>}
        {!loading && !err && items.length === 0 && <p>No menu items found.</p>}
        <ul className="menu-items">
          {items.map(item => (
            <li key={item.id} className="menu-item">
              {item.imgId && (
                <img
                  src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_208,h_208,c_fit/${item.imgId}`}
                  alt={item.name}
                />
              )}
              <div>
                <p>{item.name}</p>
                <p>â‚¹{item.price}</p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
