// src/LocationSelector.js
import React, { useState } from 'react';
import { useApp } from './context';
import './App.css'; 

const LOCATIONS = {
  'bangalore': { lat: 12.9716, lng: 77.5946 },
  'delhi': { lat: 28.7041, lng: 77.1025 },
  'mumbai': { lat: 19.0760, lng: 72.8777 },
  'dhanbad': { lat: 23.7957, lng: 86.4304 },
  'hyderabad': { lat: 17.3850, lng: 78.4867 },
    'ranchi': { lat: 23.3441, lng: 85.3096 }, // New
  'pune': { lat: 18.5204, lng: 73.8567 },      // New
  'vizag': { lat: 17.6868, lng: 83.2185 },    // New
  'chennai': { lat: 13.0827, lng: 80.2707 } 
};

const LocationSelector = () => {
  const { setUserLocation, setUserCity } = useApp();
  const [cityInput, setCityInput] = useState('');

  const handleCityChange = (e) => {
    setCityInput(e.target.value);
    const lowercaseInput = e.target.value.toLowerCase();

    if (LOCATIONS[lowercaseInput]) {
      setUserLocation(LOCATIONS[lowercaseInput]);
      setUserCity(e.target.value);
    }
  };

  return (
    <div style={{ marginLeft: 20 }}>
      <input
        type="text"
        placeholder="Enter city (e.g., Delhi, Mumbai)"
        value={cityInput}
        onChange={handleCityChange}
        className="search-input"
        style={{ width: '250px' }}
      />
    </div>
  );
};

export default LocationSelector;
