import './App.css';
import { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AppContext } from './context';

const Header = () => {
  const { isLoggedIn, logout, cartItems } = useContext(AppContext);
  const navigate = useNavigate();

  const handleAuthClick = () => {
    if (isLoggedIn) {
      logout();
    } else {
      navigate('/signin');
    }
  };

  return (
    <div className="bg-container">
      <div className="image-card">
        <img
          src="https://www.citypng.com/public/uploads/preview/fast-food-delivery-cartoon-character-logo-hd-transparent-png-735811696674987bnjkeubewg.png"
          className="image-logo"
          alt="image-logo"
        />
      </div>
      <div>
        <ul className="header-list">
          <li className="list">Search</li>
          <li className="list">Offers</li>
          <li className="list">Help</li>
          <li className="list" onClick={handleAuthClick} style={{ cursor: 'pointer' }}>
            {isLoggedIn ? 'Logout' : 'Sign In'}
          </li>
          <li className="list">Cart ({cartItems.length})</li>
        </ul>
      </div>
    </div>
  );
};

export default Header;
