// Helpers to safely extract data from Swiggy's frequently-changing structure

export const SWIGGY_BASE =
  'https://www.swiggy.com/dapi/restaurants/list/v5?is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING';

export const buildListingUrl = (lat, lng) => `${SWIGGY_BASE}&lat=${lat}&lng=${lng}`;

// Extract restaurant array
export function extractRestaurants(json) {
  const cards = json?.data?.cards || [];
  // Find first card with restaurants
  for (const c of cards) {
    const restaurants = c?.card?.card?.gridElements?.infoWithStyle?.restaurants;
    if (Array.isArray(restaurants)) return restaurants;
  }
  return [];
}

// Extract section title (e.g., "Top restaurant chains")
export function extractSectionTitle(json) {
  const cards = json?.data?.cards || [];
  for (const c of cards) {
    const title = c?.card?.card?.title || c?.card?.card?.header?.title;
    if (title) return title;
  }
  return '';
}

// Extract image grid banners (“What’s on your mind?”)
export function extractBannerImages(json) {
  const cards = json?.data?.cards || [];
  for (const c of cards) {
    const grid = c?.card?.card?.imageGridCards?.info;
    if (Array.isArray(grid)) {
      return grid.map(i => ({
        id: i.id || i.action?.link || i.imageId,
        imageId: i.imageId,
        accessibility: i.accessibility?.altText || 'banner',
      }));
    }
  }
  return [];
}
