// src/LocationSelector.js
import React, { useState } from 'react';
import { useApp } from './context';
import './App.css';

const LOCATIONS = {
  'bangalore': { lat: 12.9716, lng: 77.5946 },
  'delhi': { lat: 28.7041, lng: 77.1025 },
  'mumbai': { lat: 19.0760, lng: 72.8777 },
  'dhanbad': { lat: 23.7957, lng: 86.4304 },
  'hyderabad': { lat: 17.3850, lng: 78.4867 },
  'ranchi': { lat: 23.3441, lng: 85.3096 },
  'pune': { lat: 18.5204, lng: 73.8567 },
  'vizag': { lat: 17.6868, lng: 83.2185 },
  'chennai': { lat: 13.0827, lng: 80.2707 },
  'kolkata': { lat: 22.5726, lng: 88.3639 },
  'ahmedabad': { lat: 23.0225, lng: 72.5714 },
  'jaipur': { lat: 26.9124, lng: 75.7873 },
  'lucknow': { lat: 26.8467, lng: 80.9462 },
  'chandigarh': { lat: 30.7333, lng: 76.7794 },
  'indore': { lat: 22.7196, lng: 75.8577 },
  'bhopal': { lat: 23.2599, lng: 77.4126 },
  'patna': { lat: 25.5941, lng: 85.1376 },
};

const LocationSelector = () => {
  const { setUserLocation, setUserCity, userLocation: currentLocation } = useApp();
  const [cityInput, setCityInput] = useState('');

  const handleCityChange = (e) => {
    const input = e.target.value;
    setCityInput(input);
    const lowercaseInput = input.toLowerCase();

    const newLocation = LOCATIONS[lowercaseInput];

    if (newLocation) {
        if (newLocation.lat !== currentLocation.lat || newLocation.lng !== currentLocation.lng) {
            setUserLocation(newLocation);
            setUserCity(input);
        }
    } else {
      // Log an error if the city is not in the list
      console.error(`Error: "${input}" is not a supported city.`);
      // You can also reset the location or show an error in the UI
      // For now, the app will continue to show the last valid location.
    }
  };

  return (
    <div style={{ marginLeft: 20, position: 'relative' }}>
      <input
        type="text"
        placeholder="Enter city (e.g., Delhi, Mumbai)"
        value={cityInput}
        onChange={handleCityChange}
        className="search-input"
        style={{ width: '250px' }}
      />
    </div>
  );
};

export default LocationSelector;
