import './App.css';
import { useState, useEffect } from 'react';
import { buildListingUrl, extractBannerImages } from './api';

const BannerCard = ({ imageId, alt }) => (
  <img
    src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_660/${imageId}`}
    alt={alt}
    className="logoimage"
  />
);

const Banner = () => {
  const [list, setList] = useState([]);
  const [error, setError] = useState('');

  const fetchData = async () => {
    try {
      const resp = await fetch(buildListingUrl(23.7956531, 86.4303859));
      const data = await resp.json();
      setList(extractBannerImages(data));
    } catch (e) {
      setError('Failed to load banners');
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <div className="bannerConatiner">
        <h3>What&apos;s on your mind?</h3>
      </div>
      <div className="bannerConatiner">
        {error && <p>{error}</p>}
        {!error && list.length === 0 && <p>Loading...</p>}
        {list.map(item => (
          <BannerCard key={item.id} imageId={item.imageId} alt={item.accessibility} />
        ))}
      </div>
    </div>
  );
};

export default Banner;
