import React, { useState, useEffect } from 'react';

const RestaurantCard = () => {
  const [list, setList] = useState([]);
  const [area, setArea] = useState('');
  const [searchText, setSearchText] = useState('');  // <--- New state for search

  const fetchData = async () => {
    const response = await fetch("https://www.swiggy.com/dapi/restaurants/list/v5?lat=23.7956531&lng=86.43038589999999&is-seo-homepage-enabled=true&page_type=DESKTOP_WEB_LISTING");
    const Data = await response.json();
    setArea(Data?.data?.cards[2]?.card?.card?.title);
    setList(Data?.data?.cards[4]?.card?.card?.gridElements?.infoWithStyle?.restaurants || []);
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Filter list based on searchText (case-insensitive)
  const filteredList = list.filter((restaurant) =>
    restaurant.info.name.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div className="restaurantslist-container">
      <h2>{area}</h2>

      {/* Search input */}
      <input
        type="text"
        placeholder="Search restaurants..."
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
        style={{
          padding: '10px',
          width: '100%',
          marginBottom: '20px',
          borderRadius: '6px',
          border: '1.5px solid #ccc',
          fontSize: '16px',
        }}
        className="search-input"
      />

      <div>
        <ul className="restaurantslist">
          {/* Your filter buttons here */}
          <li className="restaurantslists">Filter</li>
          <li className="restaurantslists">Sort By</li>
          <li className="restaurantslists">Rating 4.0+</li>
          <li className="restaurantslists">Fast Delivery</li>
          <li className="restaurantslists">Pure Veg</li>
          <li className="restaurantslists">Offers</li>
          <li className="restaurantslists">Rs 300-600</li>
          <li className="restaurantslists">less than 300</li>
        </ul>
      </div>

      <div className="cardcontainer">
  {filteredList.length > 0 ? (
    filteredList.map((each) => (
      <Cards data={each} key={each.info.id} />
    ))
  ) : (
    <p>No restaurants match your search.</p>
  )}
</div>

    </div>
  );
};

export default RestaurantCard;
