// src/RestaurantMenu.js
import { useEffect, useState } from "react";
import { useParams, NavLink } from "react-router-dom";
import { fetchMenu } from "./api";
import { useApp } from "./context"; // Import the useApp hook

export default function RestaurantMenu() {
  const { id } = useParams(); // Get the restaurant ID from the URL
  const [loading, setLoading] = useState(true);
  const [items, setItems] = useState([]);
  const [err, setErr] = useState("");

  // Get the location data and addToCart function from the global context
  const { addToCart, userLocation } = useApp();

  useEffect(() => {
    // Console log to see if the component is rerendering unnecessarily
    console.log('Fetching menu for restaurant:', id);

    // Guard clause to prevent fetch on invalid id or location
    if (!id || !userLocation?.lat || !userLocation?.lng) {
      setLoading(false);
      return;
    }

    const ac = new AbortController();
    setLoading(true);

    // Use the dynamic location from the context
    fetchMenu(id, userLocation.lat, userLocation.lng, ac.signal)
      .then((json) => {
         setItems(extractMenuItems(json));
         setErr(''); // Clear any previous errors
      })
      .catch(e => {
          if (e.name === 'AbortError') {
              console.log('Fetch aborted. This is expected on cleanup.');
              return;
          }
          setErr(e.message);
      })
      .finally(() => setLoading(false));

    // Cleanup function that aborts the fetch
    return () => ac.abort();
  }, [id, userLocation]); // Dependencies: id and userLocation

  const handleAddToCart = (item) => {
      addToCart(item);
  };

  return (
    <div className="menu-page">
      <NavLink to="/" className="back-button">Back to Restaurants</NavLink>
      <h2>Restaurant Menu</h2>
      {loading && <p>Loading menu...</p>}
      {err && <p style={{ color: "#b00" }}>{err}</p>}
      {!loading && !err && items.length === 0 && <p>No menu items found.</p>}
      <ul className="menu-items">
        {items.map(item => (
          <li key={item.id} className="menu-item">
            {item.imgId && (
              <img
                src={`https://media-assets.swiggy.com/swiggy/image/upload/fl_lossy,f_auto,q_auto,w_208,h_208,c_fit/${item.imgId}`}
                alt={item.name}
              />
            )}
            <div>
              <p>{item.name}</p>
              <p>â‚¹{item.price}</p>
            </div>
            <button
              onClick={() => handleAddToCart(item)}
              className="add-to-cart-menu-btn"
            >
              Add
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

function extractMenuItems(json) {
    const sections = json?.data?.cards || [];
    const menuItems = [];

    for (const section of sections) {
        const menu = section?.card?.card?.itemCards || section?.card?.card?.carousel;
        if (menu) {
            for (const item of menu) {
                const itemData = item?.card?.info;
                if (itemData) {
                    menuItems.push({
                        id: itemData.id,
                        name: itemData.name,
                        price: (itemData.price || itemData.defaultPrice) / 100,
                        imgId: itemData.cloudinaryImageId
                    });
                }
            }
        }
    }
    return menuItems;
}
